const elCount=document.getElementById("count"),elPlusBtn=document.getElementById("plus-btn"),elMinusBtn=document.getElementById("minus-btn"),elResetBtn=document.getElementById("reset-btn");let count=0;const updateCount=e=>{e<0||(count=e,elCount.innerHTML=count.toLocaleString(),e%33==0&&0!==e&&(document.body.classList.add("animate-wave"),setTimeout(()=>document.body.classList.remove("animate-wave"),2e3)))};elPlusBtn.addEventListener("click",e=>{let t=e.pageY,n=e.pageX;updateCount(count+1);let l=document.createElement("div");l.innerHTML="+1",l.className="plus-sign",document.body.append(l),l.style.top=`${t-32}px`,l.style.left=`${n-10}px`,setTimeout(()=>l.remove(),1e3)}),elResetBtn.addEventListener("click",()=>updateCount(0)),elMinusBtn.addEventListener("click",()=>updateCount(count-1));